// Access Telegram data from trigger
const telegramData = $input.first().json;
const message = telegramData.message;
const chatId = message.chat.id;
const messageId = message.message_id;
const text = message.text || '';

// Whitelist check
const WHITELIST = ['6016705076']; // Replace with your actual chat ID
if (!WHITELIST.includes(chatId.toString())) {
  return [{
    json: {
      chatId,
      messageId,
      skipExecution: true,
      error: 'Unauthorized user'
    }
  }];
}

// Parse command
let command = '';
let commandType = 'ask';
let skipExecution = false;

if (text.startsWith('/ask ')) {
  const question = text.substring(5).trim();
  if (!question) {
    skipExecution = true;
  } else {
    commandType = 'ask';
    command = `claude -p "${question.replace(/"/g, '\\"')}" --output-format text`;
  }
} else if (text.startsWith('/run ')) {
  const task = text.substring(5).trim();
  if (!task) {
    skipExecution = true;
  } else {
    commandType = 'run';
    command = `claude -p "${task.replace(/"/g, '\\"')}" --output-format text --allowedTools "Read,Grep,Glob,Bash(git *)"`;
  }
} else if (text.startsWith('/status')) {
  commandType = 'status';
  command = 'uptime && sysctl -n vm.loadavg';
} else if (text.startsWith('/')) {
  // Unknown command
  skipExecution = true;
} else {
  // Default to ask
  const question = text.trim();
  if (!question) {
    skipExecution = true;
  } else {
    commandType = 'ask';
    command = `claude -p "${question.replace(/"/g, '\\"')}" --output-format text`;
  }
}

return [{
  json: {
    chatId,
    messageId,
    command,
    commandType,
    skipExecution
  }
}];

// Get parse data and HTTP response
const parseData = $('Parse Command & Filter').first().json;
const httpResponse = $input.first().json;

const chatId = parseData.chatId;
const messageId = parseData.messageId;
const commandType = parseData.commandType;

// Extract output from HTTP response
let output = '';

if (httpResponse.error) {
  // HTTP request failed entirely
  output = `‚ùå Server error: ${httpResponse.error}`;
} else if (httpResponse.exitCode !== 0) {
  // Command executed but failed
  output = `‚ùå Command failed (exit ${httpResponse.exitCode}):\n${httpResponse.stderr || httpResponse.stdout}`;
} else {
  // Success
  output = httpResponse.stdout || '(no output)';
}

// Add prefix based on command type
if (commandType === 'ask') {
  output = `ü§ñ Claude:\n${output}`;
} else if (commandType === 'run') {
  output = `‚öôÔ∏è Execution:\n${output}`;
} else if (commandType === 'status') {
  output = `üìä Status:\n${output}`;
}

// Truncate to 4000 chars (Telegram limit is 4096)
if (output.length > 4000) {
  output = output.substring(0, 3980) + '\n\n... (truncated)';
}

return [{
  json: {
    chatId,
    messageId,
    output
  }
}];

// Get parse data
const parseData = $input.first().json;
const chatId = parseData.chatId;
const messageId = parseData.messageId;
const error = parseData.error || 'Invalid command or empty message';

const output = `‚ùå Error: ${error}\n\nUsage:\n/ask <question> - Ask Claude\n/run <task> - Execute task\n/status - System status`;

return [{
  json: {
    chatId,
    messageId,
    output
  }
}];
